import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import ContextProvider from './ContextProvider';
import fetchPlanets from '../hooks/fetchStarsWars';

function ContextComponent({ children }) {
  const initialData = {
    filterByName: {
      name: '',
    },
  };

  const [data] = fetchPlanets();
  const [planets, setPlanets] = useState();
  const [filters, setFilters] = useState(initialData);

  useEffect(() => {
    if (data) {
      setPlanets(data.results);
    }
  }, [data]);

  const handleFilterName = ({ target: { value } }) => {
    setFilters({
      ...filters,
      filterByName: { name: value },
    });
    filterData(value);
  };

  const context = {
    planets,
    filters,
    handleFilterName,
  };

  return (
    <ContextProvider.Provider value={ context }>
      { children }
    </ContextProvider.Provider>
  );
}

ContextComponent.propTypes = {
  children: PropTypes.node.isRequired,
};

export default ContextComponent;
