import { useEffect, useState } from 'react';

function FetchPlanets() {
  const url = 'https://swapi-trybe.herokuapp.com/api/planets/';
  const [data, setData] = useState();
  const getStarsWars = () => {
    fetch(url)
      .then((response) => response.json())
      .then((result) => {
        setData(result);
      });
  };

  useEffect(() => {
    getStarsWars();
  }, []);

  return [data];
}

export default FetchPlanets;
